INSERT INTO `products_reviews` (`id`, `product_id`, `from`, `images`, `text`, `rating`, `date`, `published`) VALUES
(2, 61, 'Анна', '[\"2eeb4256b2c0c36256539f343b2ebc31.jpeg\"]', 'Я самая счастливая обладательница красивого дивана Брендон. Особенно хочу поблагодарить менеджера Евгения, который очень достойно описал все преимущества и смог меня заинтересовать именно вашей мебелью. Спасибо за уют и красоту в моем доме. Буду рекомендовать вашу компанию своим друзьям и близким. Желаю дальнейшего развития и процветания ! ', 5, 1634117163, 1),
(4, 61, 'Витек', '[\"4ef10ebfb450e7f5022b1aea4a2b3dbf.jpg\"]', 'Все ок', 5, 1634118279, 0),
(5, 61, 'Виктор', '[\"843712e8de084bc46c95eab96729618c.jpeg\"]', 'Все окей', 5, 1634118462, 0),
(6, 61, 'Проверка', '[\"a95df5c03d297808b52dea49433657e1.jpeg\"]', 'Роооооор', 5, 1634118518, 0),
(7, 61, 'Виктор', '[\"e64e6e7c062046b9f6d383f052839906.jpeg\", \"18ee74dab2293d10140a93f4a3312773.jpeg\"]', 'Попроомсп', 5, 1634118565, 0),
(8, 59, 'Виктор', NULL, 'Хороший диван', 5, 1634134449, 0),
(9, 77, 'Екатерина', '[\"5d6062b92df4e8deae684709dca1cab3.jpeg\"]', 'Самый клевый диван! Выбирали больше месяца себе диван, у мужа больная спина и важно чтобы он был с поддержкой для спины и с хорошим спальным местом, пересидели на куче диванов, но все было не то - либо спальное не достаточно удобное, но диван сам удобный, либо наоборот - диван удобный, а спальное место слишком мягкое. А у этого дивана очень продумана посадка, фабрике респект! Спальное тоже очень большое 180*200 см, можно разложить наполовину (они это называют режим релакс) и тогда очень удобно сесть и вытянуть ноги и никакие пуфики уже не нужны. Очень хочется поблагодарить молодого человека по имени Арсен (кажется ТЦ называется Фэмили Рум, рядом с Румянцево)! Очень долго с нами работал, у парня железная выдержка, все грамотно объяснил и показал, самый лучший, наверно, продавец из тех, кто попался за последнее время, сто раз разложил, собрал, пока муж попробовал посидеть и полежать во всех позах, проверить. Умничка! Фабрике тоже отдельное спасибо за то, что делают добротно и качественно. Дизайн тоже очень красивый и стильный. Вообщем мы очень рады, что наткнулись на эту модель и фабрику. Спасибо! ', 5, 1634242646, 1),
(10, 65, 'Гия', '[\"31d2e6f6ce0412d3f526482f6bc77db0.jpeg\"]', 'Приобретали диван Уолис , который мы нашли в Румере , обошли весь мебельный центр два раза, но лучше и интересней его мы не увидели. Диван нам нужен был срочно, поэтому приобрели его с выставки. Спасибо менеджеру Анжелике что все организовала быстро, доставили диван в оговорённые сроки и теперь он красуется у нас дома)) в интерьер подобран идеально. Уже  советуем  друзьям, спасибо', 5, 1634242832, 1),
(11, 80, 'Елена', '[\"538c21efd9cc92aa9c76c8c5ebf708c2.jpg\"]', 'Долго искали себе модель, чтобы была удобной,  с полноценным спальным местом и с современным и необычным дизайном. Везде одни и те же диваны стоят, на эту модель сразу обратили внимание, мужу она тоже очень понравилась, даже не стали больше ничего искать. Отдельное спасибо консультанту Арсену, ТЦ Family Room, очень добрый и вежливый парень, сразу видно давно работает и знает как общаться с покупателями, помог с выбором ткани, потому как я очень серьезно отношусь к качеству материала, у нас двое детей. У них еще салон только открылся, сделали мне приличную скидку! Берите эту модель, даже не сомневайтесь, дома выглядит еще лучше, чем в магазине!', 5, 1634565968, 1),
(12, 104, 'Юлия', '[\"04e758b24d3d989acd8db99f7b528a8e.jpg\"]', 'Для меня было важно, чтобы спальное место было ортопедическим! Спать на диване буду каждый день! \nОстановила свой выбор на диване Девон от компании Balito. Диваном пользуюсь вот уже два месяца, спина не болит…\nУ дивана есть большой короб для белья, всё помещается, а самое важное, есть планка от стенки, не затирает мои дорогие обои, диван с шагающим механизмом - не царапает пол, всё супер - я довольна! Это то, что нужно!', 5, 1634568530, 1);

